import React, { useState, useEffect } from "react";
import { Stack, Heading, Flex } from "@chakra-ui/react";
import Message from "../components/Message";
import MessageOptions from "../components/MessageOptions";
import { getPickUpLine, getRoot } from '../Api.js';

const Conversation = () => {
  const fetchOptionsInitially = (selectedOption) => {
    // Request
    // let data = await getPickUpLine({
    //     "profile1": {"name":"Cathreen", "profession":"engineer", "favorite food":"noodles"},
    //     "profile2": {"name":"Paul", "profession":"engineer", "favorite food":"noodles"},
    //     "reply_to": null,
    //     "msg_attr": [
    //         "witty",
    //         "funny",
    //         "curious to know about each other"
    //     ],
    //     "history": []
    // });

    const data = Api.getPickUpLine();
  };

  const [history, setHistory] = useState([]);
  const [options, setOptions] = useState(fetchOptionsInitially());

  const handleOptionSelection = (option) => {
    fetchOptions(option);
    setHistory([...history, option]);
  };

  const fetchOptions = (selectedOption) => {
    // Request
    // let data = await getPickUpLine({
    //     "profile1": {"name":"Cathreen", "profession":"engineer", "favorite food":"noodles"},
    //     "profile2": {"name":"Paul", "profession":"engineer", "favorite food":"noodles"},
    //     "reply_to": null,
    //     "msg_attr": [
    //         "witty",
    //         "funny",
    //         "curious to know about each other"
    //     ],
    //     "history": []
    // });

    const data = Api.getPickUpLine();
    setOptions(data['choices']);
  };

  return (
    <Stack spacing={2} w='100%'>
      <Heading as='h2' size='lg'>Conversation</Heading>
      <Flex flexDirection='column' gap={2} w='100%'>
        {history.map((message, index) => (
          <Message key={index} isUser={index % 2 === 0}>{message}</Message>
        ))}
        <MessageOptions options={options} onSelect={handleOptionSelection} />
      </Flex>
    </Stack>
  );
};

export default Conversation;

class Api {
    static COUNT = 0;

    static getPickUpLine() {
      let response = null;
      switch (Api.COUNT) {
        case 0:
          response = Api.getFirstResponse();
          break;
        case 1:
          response = Api.getSecondResponse();
          break;
        case 2:
          response = Api.getThirdResponse();
          break;
        case 3:
          response = Api.getFourthResponse();
          break;
      }

      Api.COUNT++;
      return response;
    }

    static getFirstResponse() {
        return {
            "profile1": {
                "name": "Cathreen",
                "profession": "engineer",
                "favorite food": "noodles"
            },
            "profile2": {
                "name": "Paul",
                "profession": "engineer",
                "favorite food": "noodles"
            },
            "reply_to": null,
            "msg_attr": [
                "witty",
                "funny",
                "curious to know about each other"
            ],
            "history": [],
            "choices": [
              "Noodles for dinner? Sounds like a plan!",
              "So, we have the same profession and same favorite food..guess we have more in common than we thought!",
              "What's the engineering project you're working on right now? Let's see who can solve it first!",
            ]
        };
    }

    static getSecondResponse() {
      return {
          "profile1": {
              "name": "Cathreen",
              "profession": "engineer",
              "favorite food": "noodles"
          },
          "profile2": {
              "name": "Paul",
              "profession": "engineer",
              "favorite food": "noodles"
          },
          "reply_to": "p1",
          "msg_attr": [
              "witty",
              "funny",
              "curious to know about each other"
          ],
          "history": {
            "p1": "Noodles for dinner? Sounds like a plan!"
          },
          "choices": [
            "I heard engineers like a challenge.. care to take one on?",
            "Wanna grab some noodles and chat about engineering?",
            "I think I found my engineering soulmate!"
          ]
      };
    }

    static getThirdResponse() {
      return {
          "profile1": {
              "name": "Cathreen",
              "profession": "engineer",
              "favorite food": "noodles"
          },
          "profile2": {
              "name": "Paul",
              "profession": "engineer",
              "favorite food": "noodles"
          },
          "reply_to": "p2",
          "msg_attr": [
              "witty",
              "funny",
              "curious to know about each other"
          ],
          "history": {
            "p1": "Noodles for dinner? Sounds like a plan!",
            "p2": "I think I found my engineering soulmate!"
          },
          "choices": [
            "Definitely!",
            "Yup!",
            "Of course!"
          ]
      };
    }

    static getFourthResponse() {
      return {
          "profile1": {
              "name": "Cathreen",
              "profession": "engineer",
              "favorite food": "noodles"
          },
          "profile2": {
              "name": "Paul",
              "profession": "engineer",
              "favorite food": "noodles"
          },
          "reply_to": "p1",
          "msg_attr": [
              "witty",
              "funny",
              "curious to know about each other"
          ],
          "history": {
            "p1": "Noodles for dinner? Sounds like a plan!",
            "p2": "I think I found my engineering soulmate!",
            "p1": "Definitely!"
          },
          "choices": [
            "And now?",
            "Bla!",
            "Hahah.."
          ]
      };
    }
}
