import React, { useState, useEffect } from "react";
import { Stack, Heading, Flex } from "@chakra-ui/react";
import Message from "../components/Message";
import MessageOptions from "../components/MessageOptions";
import { getPickUpLine, getRoot } from '../Api.js';

const Conversation = () => {
  const [history, setHistory] = useState([]);
  const [options, setOptions] = useState([]);

  useEffect(() => {
    fetchOptions();
  }, []);

  const handleOptionSelection = (option) => {
    fetchOptions(option);
    setHistory([...history, option]);
  };

  const fetchOptions = async (selectedOption) => {
    // Request
    // let data = await getPickUpLine({
    //     "profile1": {"name":"Cathreen", "profession":"engineer", "favorite food":"noodles"},
    //     "profile2": {"name":"Paul", "profession":"engineer", "favorite food":"noodles"},
    //     "reply_to": null,
    //     "msg_attr": [
    //         "witty",
    //         "funny",
    //         "curious to know about each other"
    //     ],
    //     "history": []
    // });

    // Response:
    const data = Api.getPickUpLine();
    if (selectedOption) {
      options = ['test3', 'test4'];
    }
    setOptions(data['choices'][0]);
  };

  return (
    <Stack spacing={2} w='100%'>
      <Heading as='h2' size='lg'>Conversation</Heading>
      <Flex flexDirection='column' gap={2} w='100%'>
        {history.map((message, index) => (
          <Message key={index} isUser={index % 2 === 0}>{message}</Message>
        ))}
        <MessageOptions options={options} onSelect={handleOptionSelection} />
      </Flex>
    </Stack>
  );
};

export default Conversation;

class Api {
    static COUNT = 0;

    static getPickUpLine() {

        Api.COUNT++;
    }

    static getFirstResponse() {
        return {
            "profile1": {
                "name": "Cathreen",
                "profession": "engineer",
                "favorite food": "noodles"
            },
            "profile2": {
                "name": "Paul",
                "profession": "engineer",
                "favorite food": "noodles"
            },
            "reply_to": null,
            "msg_attr": [
                "witty",
                "funny",
                "curious to know about each other"
            ],
            "history": [],
            "choices": [
              "Noodles for dinner? Sounds like a plan!",
              "So, we have the same profession and same favorite food..guess we have more in common than we thought!",
              "What's the engineering project you're working on right now? Let's see who can solve it first!",
            ]
        };
    }

    static getSecondResponse() {
      return {
          "profile1": {
              "name": "Cathreen",
              "profession": "engineer",
              "favorite food": "noodles"
          },
          "profile2": {
              "name": "Paul",
              "profession": "engineer",
              "favorite food": "noodles"
          },
          "reply_to": 'p2',
          "msg_attr": [
              "witty",
              "funny",
              "curious to know about each other"
          ],
          "history": {
            "p1": "Noodles for dinner? Sounds like a plan!"
          },
          "choices": [
            "I heard engineers like a challenge.. care to take one on?",
            "Wanna grab some noodles and chat about engineering?",
            "I think I found my engineering soulmate!"
          ]
      };
  }
}
