import React, { useState } from "react";
import { Heading, Container } from "@chakra-ui/react";
import Header from "../components/Header";
import Conversation from "../components/Conversation";
import MessageOptions from "../components/MessageOptions";

const Chat = () => {
  const [selectedMessage, setSelectedMessage] = useState(null);
  const [selectedResponse, setSelectedResponse] = useState(null);
  const [options, setOptions] = useState([]);

  const handleOptionSelection = (option) => {
    if (selectedMessage && !selectedResponse) {
      setSelectedResponse(option);
    } else {
      setSelectedMessage(option);
      setSelectedResponse(null);
    }
    fetchOptions(option);
  };

  const fetchOptions = async (selectedOption) => {
    const type = selectedMessage ? 'response' : 'message';
    const options = {}; // API Call
    setOptions(options);
  };

  return (
    <Container maxW='6xl'>
      <Heading as='h1' size='2xl' mb={10}>Dating AI</Heading>
      <Header />
      <Conversation />
      <MessageOptions type="message" options={options} onSelect={handleOptionSelection} />
        {selectedMessage && (
          <MessageOptions type="response" options={options} onSelect={handleOptionSelection} />
        )}
        {selectedResponse && (
          <div>
            <p>Selected message: {selectedMessage}</p>
            <p>Selected response: {selectedResponse}</p>
          </div>
        )}
    </Container>
  );
};

export default Chat;
