import { useState, useEffect } from 'react';
import { Stack, Heading, Flex } from "@chakra-ui/react";
import Message from "../components/Message";
import MessageChoices from "../components/MessageChoices";
import { getPickUpLine, getRoot } from '../Api.js';

function Conversation() {
  const [history, setHistory] = useState([]);
  const [choices, setChoices] = useState([]);

  async function fetchChoices(selectedChoice) {
    const data = await getPickUpLine({
      "profile1": {"name":"Cathreen", "profession":"engineer", "favorite food":"noodles"},
      "profile2": {"name":"Paul", "profession":"engineer", "favorite food":"noodles"},
      "sender": "p1",
      "msg_attr": [
          "witty",
          "funny",
          "curious to know about each other"
      ],
      "history": history
    });
console.log(data);
    setChoices(data['choices']);

    if (selectedChoice) {
      setHistory(prevHistory => [...prevHistory, selectedChoice]);
    }
  }

  useEffect(() => {
    async function getChoices() {
      const choices = await fetchChoices();
      setChoices(choices);
    }
    getChoices();
  }, []);

  function handleOptionSelection(choice) {
    fetchChoices(choice);
  }

  return (
    <Stack spacing={2} w='100%'>
      <Heading as='h2' size='lg'>Conversation</Heading>
      <Flex flexDirection='column' gap={2} w='100%'>
        {history.map((message, index) => (
          <Message key={index} isUser={index % 2 === 0}>
            {message}
          </Message>
        ))}
        <MessageChoices
          choices={choices}
          onSelect={handleOptionSelection}
          isUser={history.length % 2 === 0}
        />
      </Flex>
    </Stack>
  );
}

export default Conversation;

class Api {
    static COUNT = 0;

    static async getPickUpLineInitially() {
      Api.COUNT = 1;
      return Api.getFirstResponse();
    }

    static async getPickUpLine() {
      await new Promise((resolve) => setTimeout(resolve, 1000));
      let response = null;
      switch (Api.COUNT) {
        case 1:
          response = Api.getSecondResponse();
          break;
        case 2:
          response = Api.getThirdResponse();
          break;
        case 3:
          response = Api.getFourthResponse();
          break;
      }

      Api.COUNT++;
      return response;
    }

    static getFirstResponse() {
        return {
            "profile1": {
                "name": "Cathreen",
                "profession": "engineer",
                "favorite food": "noodles"
            },
            "profile2": {
                "name": "Paul",
                "profession": "engineer",
                "favorite food": "noodles"
            },
            "sender": "p1",
            "msg_attr": [
                "witty",
                "funny",
                "curious to know about each other"
            ],
            "history": [],
            "choices": [
              "Noodles for dinner? Sounds like a plan!",
              "So, we have the same profession and same favorite food..guess we have more in common than we thought!",
              "What's the engineering project you're working on right now? Let's see who can solve it first!",
            ]
        };
    }

    static getSecondResponse() {
      return {
