import { Flex } from "@chakra-ui/react";
import React, { useState } from "react";

const Chat = () => {
  const [messages, setMessages] = useState([
	{ from: "p1", text: "Hi!" },
	{ from: "p2", text: "Hey there" },
	{ from: "p1", text: "It's me!" },
	{ from: "p1", text: "Cool." },
  ]);

  const handleSelectMessage = () => {
	if (!selectedMessage.trim().length) {
  		return;
	}
	const data = selectedMessage;

	setMessages((old) => [...old, { from: "p1", text: data }]);
	setSelectedMessage("");
  };

  return (
	<Flex w="100%" h="100vh" justify="center" align="center">
  	<Flex w="40%" h="90%" flexDir="column">
    	<Messages messages={messages} />
    	<Flex w="100%" mt="5">
      	<Input
      	  placeholder="Type Something..."
      	  border="none"
      	  borderRadius="none"
      	  _focus={{
      	    border: "1px solid black",
      	  }}
      	  onKeyPress={(e) => {
      	    if (e.key === "Enter") {
      	      handleSendMessage();
      	    }
      	  }}
      	  value={selectedMessage}
      	  onChange={(e) => setSelectedMessage(e.target.value)}
      	/>
      	<Button
      	  bg="black"
      	  color="white"
      	  borderRadius="none"
      	  _hover={{
      	    bg: "white",
      	    color: "black",
      	    border: "1px solid black",
      	  }}
      	  disabled={selectedMessage.trim().length <= 0}
      	  onClick={handleSendMessage}
      	>
      	  Send
      	</Button>
    </Flex>
  	</Flex>
	</Flex>
  );
};

export default Chat;
