import React, { useState, useEffect } from "react";
import { Stack, Heading, Flex } from "@chakra-ui/react";
import Message from "../components/Message";
import MessageOptions from "../components/MessageOptions";

const Conversation = () => {
  const [selectedMessage, setSelectedMessage] = useState(null);
  const [selectedResponse, setSelectedResponse] = useState(null);
  const [options, setOptions] = useState([]);

  useEffect(() => {
    fetchOptions();
  }, []);

  const handleOptionSelection = (option) => {
    if (selectedMessage && !selectedResponse) {
      setSelectedResponse(option);
    } else {
      setSelectedMessage(option);
      setSelectedResponse(null);
    }
    fetchOptions(option);
  };

  const fetchOptions = async (selectedOption) => {
    const options = ['test1', 'test2']; // API Call
    setOptions(options);
  };

  return (
    <Stack spacing={2} w='100%'>
      <Heading as='h2' size='lg'>Conversation</Heading>
      <Flex flexDirection='column' gap={2} w='100%'>
        <MessageOptions options={options} onSelect={handleOptionSelection} />
          {selectedMessage && (
            <MessageOptions options={options} onSelect={handleOptionSelection} />
          )}
          {selectedResponse && (
            <div>
              <p>Selected message: {selectedMessage}</p>
              <p>Selected response: {selectedResponse}</p>
            </div>
          )}
        <Message />
        <Message isUser='true'/>
      </Flex>
    </Stack>
  );
};

export default Conversation;
